import { click } from '@testing-library/user-event/dist/click';
import { FcLike, FcLikePlaceholder } from 'react-icons/fc';
import { toast } from 'react-toastify';


 import React from 'react'

 const Card = (props) => {       //**********()  vs {}
    let course = props.course;
    let likedCourses = props.likedCourses;
    let setLikedCourses = props.setLikedCourses;

    function clickHandler () {
      if(likedCourses.includes(course.id)) {
        //pehle se liked ho chuka hai
        setLikedCourses( (prev) => prev.filter( (cid!== course.id)))
      }
      else {
        //pehle se liked nahi hai ye course
        //insert karna hai ye liked course me
        if(likedCourses.length === 0) {
          setLikedCourses([course.id]);
        }
        else {
          //non empty pehle se
          setLikedCourses( (prev) => [...prev, course.id])
        }
      }
    }

   return (
     <div className='w-[300px] bg-bgDark bg-opacity-80 rounded-md overflow-hidden'>
        <div className='relative '>
            <img src={course.image.url}></img>

            <div className='w-[40px] h-[40px] bg-white rounded-full  absolute right-2 bottom-1
            grid place-items-center '>
                <button onClick={clickHandler}>
                <FcLike fontSize="1.75rem"/>
                </button>
            </div>    
        </div>

        <div className='p-4'>
            <p className='text-white font-semibold text-lg leading-6'>{course.title}</p>
            <p className='mt-2 text-white'>{course.description}</p>
        </div>
     </div>
   )
 }

 export default Card
